// Online Bookstore System (OBS) Deliverable 3 Starter
// React + Tailwind + Shadcn UI ready for deployment and demo
// Covers Home, Book Details, Registration, Login, Edit Profile, Cart, Checkout, Order History, Admin screens with basic routing and UI structure
// Uses a mocked books list for now (swap with DB fetch)

import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';

export default function App() {
  const books = [
    { id: 1, title: 'Clean Code', author: 'Robert C. Martin', genre: 'Programming' },
    { id: 2, title: 'The Pragmatic Programmer', author: 'Andrew Hunt', genre: 'Programming' },
  ];

  return (
    <Router>
      <div className="p-4">
        <nav className="flex space-x-4 mb-4">
          <Link to="/">Home</Link>
          <Link to="/register">Register</Link>
          <Link to="/login">Login</Link>
          <Link to="/edit-profile">Edit Profile</Link>
          <Link to="/cart">Cart</Link>
          <Link to="/checkout">Checkout</Link>
          <Link to="/order-history">Order History</Link>
          <Link to="/admin">Admin</Link>
        </nav>
        <Routes>
          <Route path="/" element={<Home books={books} />} />
          <Route path="/book/:id" element={<BookDetails books={books} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route path="/edit-profile" element={<EditProfile />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="/order-history" element={<OrderHistory />} />
          <Route path="/admin" element={<Admin />} />
        </Routes>
      </div>
    </Router>
  );
}

function Home({ books }) {
  return (
    <div>
      <h1 className="text-xl font-bold mb-2">Home - Featured Books</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {books.map(book => (
          <Card key={book.id}>
            <CardContent>
              <h2 className="font-semibold">{book.title}</h2>
              <p>Author: {book.author}</p>
              <p>Genre: {book.genre}</p>
              <Link to={`/book/${book.id}`}>
                <Button className="mt-2">View Details</Button>
              </Link>
              <Button className="mt-2">Add to Cart</Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

function BookDetails({ books }) {
  const id = window.location.pathname.split('/').pop();
  const book = books.find(b => b.id === parseInt(id));
  return book ? (
    <div>
      <h1 className="text-xl font-bold">{book.title}</h1>
      <p>Author: {book.author}</p>
      <p>Genre: {book.genre}</p>
      <Button className="mt-2">Add to Cart</Button>
    </div>
  ) : (
    <p>Book not found.</p>
  );
}

function Register() {
  return (
    <div>
      <h1 className="text-xl font-bold">Register</h1>
      <p>[Registration form with mandatory fields here]</p>
    </div>
  );
}

function Login() {
  return (
    <div>
      <h1 className="text-xl font-bold">Login</h1>
      <p>[Login form here]</p>
    </div>
  );
}

function EditProfile() {
  return (
    <div>
      <h1 className="text-xl font-bold">Edit Profile</h1>
      <p>[Edit personal info, password, payment, and address here]</p>
    </div>
  );
}

function Cart() {
  return (
    <div>
      <h1 className="text-xl font-bold">Shopping Cart</h1>
      <p>[Cart items and quantity adjustment here]</p>
    </div>
  );
}

function Checkout() {
  return (
    <div>
      <h1 className="text-xl font-bold">Checkout</h1>
      <p>[Display saved payment card, shipping address, and order summary]</p>
    </div>
  );
}

function OrderHistory() {
  return (
    <div>
      <h1 className="text-xl font-bold">Order History</h1>
      <p>[Past orders with reorder option here]</p>
    </div>
  );
}

function Admin() {
  return (
    <div>
      <h1 className="text-xl font-bold">Admin Panel</h1>
      <ul className="list-disc ml-6">
        <li>Manage Books</li>
        <li>Manage Users</li>
        <li>Manage Promotions</li>
      </ul>
    </div>
  );
}
