import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import { FaUser, FaShoppingCart, FaSignInAlt, FaSearch } from 'react-icons/fa';
import { useState } from 'react';

export default function App() {
  const [search, setSearch] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const [orders, setOrders] = useState([]);

  const featuredBooks = [
    { id: 1, title: 'Clean Code', author: 'Robert C. Martin', genre: 'Programming', price: 30 },
    { id: 2, title: 'The Pragmatic Programmer', author: 'Andrew Hunt', genre: 'Programming', price: 35 },
  ];

  const comingSoonBooks = [
    { id: 3, title: 'AI for Everyone', author: 'Andrew Ng', genre: 'Technology', price: 40 },
    { id: 4, title: 'Next.js in Action', author: 'Vercel Team', genre: 'Web Development', price: 45 },
  ];

  const handleAddToCart = (book) => {
    setCartItems(prev => {
      const existing = prev.find(item => item.id === book.id);
      if (existing) {
        return prev.map(item => item.id === book.id ? { ...item, quantity: item.quantity + 1 } : item);
      } else {
        return [...prev, { ...book, quantity: 1 }];
      }
    });
  };

  const handleQuantityChange = (id, delta) => {
    setCartItems(prev =>
      prev
        .map(item => item.id === id ? { ...item, quantity: item.quantity + delta } : item)
        .filter(item => item.quantity > 0)
    );
  };

  return (
    <Router>
      <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
        <header style={{ background: '#4a90e2', color: 'white', padding: '15px', borderRadius: '8px', marginBottom: '20px' }}>
          <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>
            <h1>📚 Online Bookstore System</h1>
          </Link>
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap', alignItems: 'center' }}>
            <Link style={navStyle} to="/login"><FaSignInAlt /> Login</Link>
            <Link style={navStyle} to="/register">Register</Link>
            <Link style={navStyle} to="/profile"><FaUser /> Profile</Link>
            <Link style={navStyle} to="/order-history">Order History</Link>
            <Link style={navStyle} to="/admin">Admin</Link>
            <Link style={navStyle} to="/cart"><FaShoppingCart /> Cart ({cartItems.reduce((sum, item) => sum + item.quantity, 0)})</Link>
            <div style={{ display: 'flex', background: 'white', borderRadius: '5px', padding: '5px 10px', alignItems: 'center' }}>
              <FaSearch color="#4a90e2" />
              <input
                type="text"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                placeholder="Search books..."
                style={{ border: 'none', outline: 'none', marginLeft: '5px' }}
              />
            </div>
          </div>
        </header>

        <Routes>
          <Route path="/" element={<Home featuredBooks={featuredBooks} comingSoonBooks={comingSoonBooks} handleAddToCart={handleAddToCart} />} />
          <Route path="/cart" element={<CartPage cartItems={cartItems} handleQuantityChange={handleQuantityChange} />} />
          <Route path="/checkout" element={<CheckoutPage cartItems={cartItems} setCartItems={setCartItems} setOrders={setOrders} />} />
          <Route path="/order-history" element={<OrderHistoryPage orders={orders} setCartItems={setCartItems} />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/profile" element={<ProfilePage />} />
          <Route path="/admin" element={<AdminPage />} />
        </Routes>
      </div>
    </Router>
  );
}

function Home({ featuredBooks, comingSoonBooks, handleAddToCart }) {
  const cardStyle = {
    background: '#fff',
    padding: '15px',
    borderRadius: '8px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    textAlign: 'center',
  };

  return (
    <div>
      <h2 style={{ color: '#4a90e2' }}>📘 Featured Books</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(180px, 1fr))', gap: '15px' }}>
        {featuredBooks.map(book => (
          <div key={book.id} style={cardStyle}>
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <p>${book.price}</p>
            <button style={buttonStyle} onClick={() => handleAddToCart(book)}>Add to Cart</button>
          </div>
        ))}
      </div>
      <h2 style={{ color: '#50c878', marginTop: '20px' }}>📗 Coming Soon</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(180px, 1fr))', gap: '15px' }}>
        {comingSoonBooks.map(book => (
          <div key={book.id} style={cardStyle}>
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <p>Coming Soon</p>
          </div>
        ))}
      </div>
    </div>
  );
}

function CartPage({ cartItems, handleQuantityChange }) {
  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div>
      <h2 style={{ color: '#4a90e2', marginBottom: '15px' }}>🛒 Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is currently empty.</p>
      ) : (
        <div style={{ maxWidth: '600px', margin: '0 auto' }}>
          {cartItems.map(item => (
            <div key={item.id} style={{
              background: '#fff',
              padding: '15px',
              marginBottom: '10px',
              borderRadius: '8px',
              boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}>
              <div>
                <h4 style={{ margin: '0 0 5px 0' }}>{item.title}</h4>
                <p style={{ margin: 0 }}>${item.price} x {item.quantity} = ${(item.price * item.quantity).toFixed(2)}</p>
              </div>
              <div>
                <button onClick={() => handleQuantityChange(item.id, -1)} style={buttonStyle}>-</button>
                <button onClick={() => handleQuantityChange(item.id, 1)} style={{ ...buttonStyle, marginLeft: '5px' }}>+</button>
              </div>
            </div>
          ))}
          <div style={{ textAlign: 'right', marginTop: '10px' }}>
            <h3>Total: ${total.toFixed(2)}</h3>
            <Link to="/checkout">
              <button style={{ ...buttonStyle, padding: '10px 20px', fontSize: '16px', marginTop: '10px' }}>
                Proceed to Checkout
              </button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
}

function CheckoutPage({ cartItems, setCartItems, setOrders }) {
  const [paymentInfo, setPaymentInfo] = useState('Visa **** 1234');
  const [address, setAddress] = useState('123 Main St, City, State');
  const [email, setEmail] = useState('user@example.com');
  const [orderConfirmed, setOrderConfirmed] = useState(false);

  const totalBeforeTax = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const tax = totalBeforeTax * 0.07;
  const totalAfterTax = totalBeforeTax + tax;

  const handleConfirmOrder = () => {
    const newOrder = {
      id: Date.now(),
      items: cartItems,
      total: totalAfterTax,
      email: email,
      paymentInfo: paymentInfo,
      address: address,
      date: new Date().toLocaleString()
    };
    setOrders(prev => [...prev, newOrder]);
    setCartItems([]);
    setOrderConfirmed(true);
  };

  if (cartItems.length === 0 && !orderConfirmed) {
    return <p>Your cart is empty. Add items to checkout.</p>;
  }

  return (
    <div>
      <h2>Checkout</h2>

      {orderConfirmed ? (
        <div>
          <h3>✅ Order Confirmed!</h3>
          <p>A confirmation email has been sent to {email}.</p>
        </div>
      ) : (
        <>
          <p><strong>Payment Info:</strong> {paymentInfo} <button onClick={() => {
            const newPayment = prompt('Enter new payment info:', paymentInfo);
            if (newPayment) setPaymentInfo(newPayment);
          }}>Change</button></p>

          <p><strong>Shipping Address:</strong> {address} <button onClick={() => {
            const newAddress = prompt('Enter new shipping address:', address);
            if (newAddress) setAddress(newAddress);
          }}>Change</button></p>

          <p><strong>Email:</strong> {email} <button onClick={() => {
            const newEmail = prompt('Enter new email address:', email);
            if (newEmail) setEmail(newEmail);
          }}>Change</button></p>

          <h3>Order Summary:</h3>
          {cartItems.map(item => (
            <p key={item.id}>{item.title} x {item.quantity} - ${item.price * item.quantity}</p>
          ))}
          <p><strong>Subtotal:</strong> ${totalBeforeTax.toFixed(2)}</p>
          <p><strong>Tax (7%):</strong> ${tax.toFixed(2)}</p>
          <p><strong>Total:</strong> ${totalAfterTax.toFixed(2)}</p>

          <button onClick={handleConfirmOrder} style={buttonStyle}>Confirm Order</button>
        </>
      )}
    </div>
  );
}

function OrderHistoryPage({ orders, setCartItems }) {
  const handleReorder = (order) => {
    setCartItems(order.items);
    alert('Items from this order have been added to your cart for reorder.');
  };

  return (
    <div>
      <h2>Order History</h2>
      {orders.length === 0 ? (
        <p>No past orders yet.</p>
      ) : (
        orders.map(order => (
          <div key={order.id} style={{
            background: '#fff',
            padding: '15px',
            borderRadius: '8px',
            boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
            marginBottom: '10px'
          }}>
            <h4>Order #{order.id} ({order.date})</h4>
            <p><strong>Email:</strong> {order.email}</p>
            <p><strong>Payment:</strong> {order.paymentInfo}</p>
            <p><strong>Address:</strong> {order.address}</p>
            {order.items.map(item => (
              <p key={item.id}>{item.title} x {item.quantity} - ${item.price * item.quantity}</p>
            ))}
            <p><strong>Total Paid:</strong> ${order.total.toFixed(2)}</p>
            <button onClick={() => handleReorder(order)} style={buttonStyle}>Reorder</button>
          </div>
        ))
      )}
    </div>
  );
}

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    alert(`Logged in as ${email}`);
    navigate('/');
  };

  return (
    <div>
      <h2>Login</h2>
      <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={inputStyle} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} style={inputStyle} />
      <button onClick={handleLogin} style={buttonStyle}>Login</button>
    </div>
  );
}

function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleRegister = () => {
    alert(`Account created for ${email}`);
    navigate('/login');
  };

  return (
    <div>
      <h2>Register</h2>
      <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={inputStyle} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} style={inputStyle} />
      <button onClick={handleRegister} style={buttonStyle}>Register</button>
    </div>
  );
}

function ProfilePage() {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [payment, setPayment] = useState('');

  const handleSave = () => {
    alert('Profile saved!');
  };

  return (
    <div>
      <h2>Edit Profile</h2>
      <input placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} style={inputStyle} />
      <input placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} style={inputStyle} />
      <input placeholder="Payment Info" value={payment} onChange={(e) => setPayment(e.target.value)} style={inputStyle} />
      <button onClick={handleSave} style={buttonStyle}>Save</button>
    </div>
  );
}

