// Updated App.jsx without Shadcn UI and ready to run

import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

export default function App() {
  const books = [
    { id: 1, title: 'Clean Code', author: 'Robert C. Martin', genre: 'Programming' },
    { id: 2, title: 'The Pragmatic Programmer', author: 'Andrew Hunt', genre: 'Programming' },
  ];

  return (
    <Router>
      <div style={{ padding: '20px' }}>
        <nav style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
          <Link to="/">Home</Link>
          <Link to="/register">Register</Link>
          <Link to="/login">Login</Link>
          <Link to="/edit-profile">Edit Profile</Link>
          <Link to="/cart">Cart</Link>
          <Link to="/checkout">Checkout</Link>
          <Link to="/order-history">Order History</Link>
          <Link to="/admin">Admin</Link>
        </nav>
        <Routes>
          <Route path="/" element={<Home books={books} />} />
          <Route path="/register" element={<div>Register Page</div>} />
          <Route path="/login" element={<div>Login Page</div>} />
          <Route path="/edit-profile" element={<div>Edit Profile Page</div>} />
          <Route path="/cart" element={<div>Cart Page</div>} />
          <Route path="/checkout" element={<div>Checkout Page</div>} />
          <Route path="/order-history" element={<div>Order History Page</div>} />
          <Route path="/admin" element={<div>Admin Page</div>} />
        </Routes>
      </div>
    </Router>
  );
}

function Home({ books }) {
  return (
    <div>
      <h1>Home - Featured Books</h1>
      <div style={{ display: 'grid', gap: '10px' }}>
        {books.map(book => (
          <div key={book.id} style={{ border: '1px solid #ccc', padding: '10px', borderRadius: '5px' }}>
            <h2>{book.title}</h2>
            <p>Author: {book.author}</p>
            <p>Genre: {book.genre}</p>
            <button style={{ marginTop: '5px' }}>View Details</button>
            <button style={{ marginTop: '5px', marginLeft: '5px' }}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}
