import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import { FaUser, FaShoppingCart, FaSignInAlt, FaSearch } from 'react-icons/fa';
import { useState } from 'react';

export default function App() {
  const [search, setSearch] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const [orders, setOrders] = useState([]);

  const featuredBooks = [
    { id: 1, title: 'Clean Code', author: 'Robert C. Martin', genre: 'Programming', price: 30 },
    { id: 2, title: 'The Pragmatic Programmer', author: 'Andrew Hunt', genre: 'Programming', price: 35 },
  ];

  const comingSoonBooks = [
    { id: 3, title: 'AI for Everyone', author: 'Andrew Ng', genre: 'Technology', price: 40 },
    { id: 4, title: 'Next.js in Action', author: 'Vercel Team', genre: 'Web Development', price: 45 },
  ];

  const handleAddToCart = (book) => {
    setCartItems(prev => {
      const existing = prev.find(item => item.id === book.id);
      if (existing) {
        return prev.map(item => item.id === book.id ? { ...item, quantity: item.quantity + 1 } : item);
      } else {
        return [...prev, { ...book, quantity: 1 }];
      }
    });
  };

  const handleQuantityChange = (id, delta) => {
    setCartItems(prev =>
      prev
        .map(item => item.id === id ? { ...item, quantity: item.quantity + delta } : item)
        .filter(item => item.quantity > 0)
    );
  };

  return (
    <Router>
      <div style={{ fontFamily: 'Arial, sans-serif', background: '#f9f9f9', minHeight: '100vh', padding: '20px' }}>
        <header style={{ background: '#4a90e2', color: '#fff', padding: '10px 20px', borderRadius: '8px', marginBottom: '20px' }}>
          <Link to="/" style={{ textDecoration: 'none', color: 'white' }}>
            <h1>📚 Online Bookstore System</h1>
          </Link>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px', flexWrap: 'wrap' }}>
            <Link style={navStyle} to="/login"><FaSignInAlt /> Login</Link>
            <Link style={navStyle} to="/register">Register</Link>
            <Link style={navStyle} to="/profile"><FaUser /> Profile</Link>
            <Link style={navStyle} to="/order-history">Order History</Link>
            <Link style={navStyle} to="/cart"><FaShoppingCart /> Cart ({cartItems.reduce((sum, item) => sum + item.quantity, 0)})</Link>
            <div style={{ display: 'flex', alignItems: 'center', background: '#fff', borderRadius: '5px', padding: '5px 10px' }}>
              <FaSearch color="#4a90e2" />
              <input
                type="text"
                placeholder="Search books..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                style={{ border: 'none', outline: 'none', marginLeft: '5px' }}
              />
            </div>
          </div>
        </header>

        <Routes>
          <Route path="/" element={<Home featuredBooks={featuredBooks} comingSoonBooks={comingSoonBooks} handleAddToCart={handleAddToCart} />} />
          <Route path="/cart" element={<CartPage cartItems={cartItems} handleQuantityChange={handleQuantityChange} />} />
          <Route path="/checkout" element={<CheckoutPage cartItems={cartItems} setCartItems={setCartItems} setOrders={setOrders} />} />
          <Route path="/order-history" element={<OrderHistoryPage orders={orders} setCartItems={setCartItems} />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/profile" element={<ProfilePage />} />
        </Routes>
      </div>
    </Router>
  );
}

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    alert(`Logged in as: ${email}`);
    navigate('/');
  };

  return (
    <div>
      <h2>Login</h2>
      <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={inputStyle} />
      <input placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} style={inputStyle} />
      <button onClick={handleLogin} style={buttonStyle}>Login</button>
    </div>
  );
}

function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleRegister = () => {
    alert(`Account created for: ${email}`);
    navigate('/login');
  };

  return (
    <div>
      <h2>Create Account</h2>
      <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={inputStyle} />
      <input placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} style={inputStyle} />
      <button onClick={handleRegister} style={buttonStyle}>Register</button>
    </div>
  );
}

function ProfilePage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [paymentInfo, setPaymentInfo] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSave = () => {
    if (password && password !== confirmPassword) {
      alert("Passwords do not match.");
      return;
    }
    alert("Profile updated successfully!");
  };

  return (
    <div>
      <h2>Edit Profile</h2>
      <input placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} style={inputStyle} />
      <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={inputStyle} />
      <input placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} style={inputStyle} />
      <input placeholder="Payment Info" value={paymentInfo} onChange={(e) => setPaymentInfo(e.target.value)} style={inputStyle} />
      <h3>Change Password</h3>
      <input placeholder="New Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} style={inputStyle} />
      <input placeholder="Confirm Password" type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} style={inputStyle} />
      <button onClick={handleSave} style={buttonStyle}>Save Changes</button>
    </div>
  );
}

function CheckoutPage({ cartItems, setCartItems, setOrders }) {
  const [paymentInfo, setPaymentInfo] = useState('Visa **** 1234');
  const [address, setAddress] = useState('123 Main St, City, State');
  const [email, setEmail] = useState('user@example.com');
  const [showConfirm, setShowConfirm] = useState(false);

  const totalBeforeTax = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const tax = totalBeforeTax * 0.07;
  const totalAfterTax = totalBeforeTax + tax;

  const handleConfirmOrder = () => {
    setOrders(prev => [...prev, { id: Date.now(), items: cartItems, total: totalAfterTax }]);
    setCartItems([]);
    setShowConfirm(true);
  };

  if (cartItems.length === 0) {
    return <p>Your cart is empty.</p>;
  }

  return (
    <div>
      <h2>Checkout</h2>
      <p><strong>Payment:</strong> {paymentInfo} <button onClick={() => setPaymentInfo(prompt('Enter new payment info', paymentInfo) || paymentInfo)}>Change</button></p>
      <p><strong>Shipping Address:</strong> {address} <button onClick={() => setAddress(prompt('Enter new address', address) || address)}>Change</button></p>
      <p><strong>Email:</strong> {email} <button onClick={() => setEmail(prompt('Enter new email', email) || email)}>Change</button></p>

      <h3>Order Summary:</h3>
      {cartItems.map(item => (
        <p key={item.id}>{item.title} x {item.quantity} - ${item.price * item.quantity}</p>
      ))}
      <p><strong>Total Before Tax:</strong> ${totalBeforeTax.toFixed(2)}</p>
      <p><strong>Tax (7%):</strong> ${tax.toFixed(2)}</p>
      <p><strong>Total After Tax:</strong> ${totalAfterTax.toFixed(2)}</p>
      <button onClick={handleConfirmOrder} style={buttonStyle}>Confirm Order</button>

      {showConfirm && (
        <div>
          <h3>Thank you for your order!</h3>
          <p>A confirmation email has been sent to {email}.</p>
        </div>
      )}
    </div>
  );
}

function OrderHistoryPage({ orders, setCartItems }) {
  const handleReorder = (items) => {
    setCartItems(items);
    alert('Items added to cart for reorder.');
  };

  return (
    <div>
      <h2>Order History</h2>
      {orders.length === 0 ? <p>No orders yet.</p> :
        orders.map(order => (
          <div key={order.id} style={{ background: '#fff', padding: '10px', marginBottom: '10px', borderRadius: '8px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
            <h4>Order #{order.id}</h4>
            {order.items.map(item => (
              <p key={item.id}>{item.title} x {item.quantity} - ${item.price * item.quantity}</p>
            ))}
            <p><strong>Total Paid:</strong> ${order.total.toFixed(2)}</p>
            <button onClick={() => handleReorder(order.items)} style={buttonStyle}>Reorder</button>
          </div>
        ))
      }
    </div>
  );
}

function Home({ featuredBooks, comingSoonBooks, handleAddToCart }) {
  const cardStyle = {
    background: '#fff',
    borderRadius: '10px',
    padding: '15px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    transition: 'transform 0.2s',
    cursor: 'pointer',
  };

  return (
    <div>
      <h2 style={{ color: '#4a90e2' }}>📘 Featured Books</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))', gap: '15px' }}>
        {featuredBooks.map(book => (
          <div
            key={book.id}
            style={cardStyle}
            onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.02)'}
            onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
          >
            <h3>{book.title}</h3>
            <p>by {book.author}</p>
            <p><strong>${book.price}</strong></p>
            <button style={buttonStyle} onClick={() => handleAddToCart(book)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

function CartPage({ cartItems, handleQuantityChange }) {
  return (
    <div>
      <h2>🛒 Your Cart</h2>
      {cartItems.length === 0 ? <p>Your cart is empty.</p> :
        cartItems.map(item => (
          <div key={item.id} style={{ background: '#fff', padding: '10px', borderRadius: '8px', marginBottom: '10px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
            <p>{item.title} - ${item.price} x {item.quantity}</p>
            <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
            <button onClick={() => handleQuantityChange(item.id, 1)} style={{ marginLeft: '5px' }}>+</button>
          </div>
        ))
      }
    </div>
  );
}

const navStyle = {
  background: '#fff',
  padding: '5px 10px',
  borderRadius: '5px',
  textDecoration: 'none',
  color: '#4a90e2',
  fontWeight: 'bold',
  display: 'flex',
  alignItems: 'center',
  gap: '5px',
};

const buttonStyle = {
  background: '#4a90e2',
  color: '#fff',
  border: 'none',
  padding: '8px 12px',
  borderRadius: '5px',
  cursor: 'pointer',
};

const inputStyle = {
  display: 'block',
  margin: '10px 0',
  padding: '8px',
  borderRadius: '5px',
  border: '1px solid #ccc',
  width: '250px',
};
